/*!
 *  学习使用的Person模块。
 */
(function () {
    function Person(name, age) {
        this.name = name;
        this.age = age;
    }
    Person.prototype.say = function () {
        return "我是：" + this.name + ",年龄:" + this.age;
    }
    //Nodejs
    if (typeof module != "undefined" && module.exports) {
        module.exports = Person;
    }
    //AMD / RequireJS
    else if (typeof define !== 'undefined' && define.amd) {
        define([], function () {
            return Person;
        });
    }
})();
